let currentQuestionIndex = 0;
let score = 0;
let userAnswers = [];

const quizContainer = document.getElementById('quiz');
const nextButton = document.getElementById('next');
const resultContainer = document.getElementById('result');
const scoreContainer = document.getElementById('score');
const answersContainer = document.getElementById('answers');
const answers = document.getElementById('answers-container');



function displayQuestion() {

    const currentQuestion = quizData[currentQuestionIndex];
    quizContainer.innerHTML = `
        <img class="image" src="${currentQuestion.image}"/>
        <div class="question">${currentQuestion.question}</div>
        <ul class="options">
            ${currentQuestion.options.map((option, index) => `
                <li>
                    <input type="radio" name="answer" id="option${index}" value="${option.isCorrect}">
                    <label for="option${index}">${option.answer}</label>
                </li>
            `).join('')}
        </ul>
    `;
}


nextButton.addEventListener('click', () => {
    const selectedOption = document.querySelector('input[name="answer"]:checked');
    if (!selectedOption) {
        alert('Please select an answer!');
        return;
    }

    const isCorrect = selectedOption.value === 'true';
    userAnswers.push({
        question: quizData[currentQuestionIndex].question,
        selected: selectedOption.nextElementSibling.textContent,
        correctAnswer: quizData[currentQuestionIndex].options.find(option => option.isCorrect).answer,
        isCorrect: isCorrect
    });

    if (isCorrect) {
        score++;
    }

    currentQuestionIndex++;
    if (currentQuestionIndex < quizData.length) {
        displayQuestion();
    } else {
        displayResult();
    }
});


function displayResult() {
    quizContainer.innerHTML = '';
    nextButton.style.display = 'none';

    scoreContainer.innerHTML = `<p>You scored ${score} out of ${quizData.length}</p>`;

    const resultHTML = userAnswers.map((answer, index) => `
        <div class="result-item">
            <p><strong>Question ${index + 1}:</strong> ${answer.question}</p>
            <p style="color: ${answer.isCorrect ? 'green' : 'red'}"><strong>Your answer:</strong> ${answer.selected} ${answer.isCorrect ? '(Correct)' : '(Incorrect)'}</p>
            ${!answer.isCorrect ? `<p><strong>Correct answer:</strong> ${answer.correctAnswer}</p>` : ''}
        </div>
        <hr>
    `).join('');

    answers.style.display = 'block'
    answersContainer.innerHTML = resultHTML;
}

displayQuestion();

